DROP DATABASE IF EXISTS fec;

CREATE DATABASE fec;

USE fec;

CREATE TABLE product_description (
    id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    description VARCHAR(500) NOT NULL,
    title VARCHAR(100) NOT NULL
);

